/* Reset basic styles */
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

body {
    font-family: 'Arial', sans-serif;
    line-height: 1.6;
    background-color: #f4f4f4;
    color: #333;
}

/* Header */
.header {
    background-color: #333;
    color: white;
    padding: 20px 0;
}

.header .container {
    display: flex;
    justify-content: space-between;
    align-items: center;
    max-width: 1200px;
    margin: 0 auto;
    padding: 0 20px;
}

.header nav ul {
    list-style-type: none;
    display: flex;
}

.header nav ul li {
    margin-left: 20px;
}

.header nav ul li a {
    color: white;
    text-decoration: none;
    font-size: 18px;
    padding: 10px;
    transition: all 0.3s ease;
}

.header nav ul li a:hover {
    background-color: #555;
    border-radius: 5px;
}

/* Hero Section */
.hero {
    background: url('https://via.placeholder.com/1500x600') no-repeat center center/cover;
    color: white;
    text-align: center;
    padding: 100px 20px;
}

.hero h2 {
    font-size: 3em;
    margin-bottom: 20px;
}

.hero p {
    font-size: 1.5em;
    margin-bottom: 30px;
}

.hero .cta-btn {
    background-color: #333;
    color: white;
    padding: 15px 30px;
    text-decoration: none;
    font-size: 18px;
    border-radius: 5px;
}

.hero .cta-btn:hover {
    background-color: #555;
}

/* Services Section */
.services {
    background-color: #fff;
    padding: 50px 20px;
}

.services .container {
    max-width: 1200px;
    margin: 0 auto;
    text-align: center;
}

.services .service-cards {
    display: flex;
    justify-content: space-around;
    margin-top: 30px;
}

.services .card {
    background-color: #f9f9f9;
    padding: 30px;
    border-radius: 5px;
    width: 30%;
    box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
    transition: all 0.3s ease;
}

.services .card:hover {
    transform: translateY(-10px);
}

.services .card h3 {
    font-size: 1.8em;
    margin-bottom: 15px;
}

.services .card p {
    font-size: 1.1em;
}

/* About Section */
.about {
    padding: 50px 20px;
    background-color: #f4f4f4;
    text-align: center;
}

.about h2 {
    font-size: 2.5em;
    margin-bottom: 20px;
}

.about p {
    font-size: 1.2em;
    max-width: 900px;
    margin: 0 auto;
}

/* Contact Section */
.contact {
    background-color: #fff;
    padding: 50px 20px;
}

.contact .container {
    max-width: 1200px;
    margin: 0 auto;
    text-align: center;
}

.contact form {
    max-width: 600px;
    margin: 0 auto;
}

.contact .form-group {
    margin-bottom: 20px;
    text-align: left;
}

.contact .form-group label {
    font-size: 1.1em;
    margin-bottom: 10px;
    display: block;
}

.contact .form-group input,
.contact .form-group textarea {
    width: 100%;
    padding: 10px;
    border-radius: 5px;
    border: 1px solid #ddd;
}

.contact .form-group textarea {
    resize: vertical;
}

.contact .submit-btn {
    background-color: #333;
    color: white;
    padding: 15px 30px;
    border: none;
    border-radius: 5px;
    font-size: 18px;
    cursor: pointer;
    transition: background-color 0.3s ease;
}

.contact .submit-btn:hover {
    background-color: #555;
}

/* Footer Section */
.footer {
    background-color: #333;
    color: white;
    padding: 20px 0;
    text-align: center;
}
